import { Component } from 'react';

import MarvelService from '../services/MarvelService';
import Spinner from '../spinner/Spinner'; 
import ErrorMessage from '../error/ErrorMessage'; 
import Skeleton from  '../skeleton/Skeleton'; 

import './charInfo.scss';

class CharInfo extends Component {
    state = {
        char: null, 
        loading: false, 
        error: false,
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.updateChar(); 
    }

    componentDidUpdate(prevProps, prevState) { 
        if (this.props.charId !== prevProps.charId) { 
            this.updateChar();
        }
    }

    // Если у нас появится ошибку, то сломается весь интерфейс и выведет белый экран (ошибка показывается, только в режиме разработки - это оверлей)
    // Нам нужно сделать, чтобы ломался только тот компонент, который вызвал ошибку

    // Используя хук ошибки, хук не будет работать, после обновления реакта с 16 версии поведение этого хука было изменено (раньше при использовании этого хука крашился только тот компонент, который вызвал ошибку, но сейчас крашится весь интерфейс)
    // componentDidCatch(err, info) {
    //     console.log(err, info);
    //     this.setState({
    //         error: true,
    //     });
    // }

    // Лучше использовать предохранители (Error Boundaries), чтобы перехватывать такие ошибки 

    updateChar = () => {
        const {charId} = this.props; 
        if (!charId) { 
            return;
        } 
        this.onCharLoading(); 
        this.marvelService 
            .getCharacter(charId) 
            .then(this.onCharLoaded) 

        this.foo.test = 0; // впишем в свойство, которого нет значение 0, чтобы вызвать ошибку
    }

    onCharLoaded = (char) => {
        this.setState({
            char,
            loading: false,
        });
    }


    onCharLoading = () => { 
        this.setState({
            loading: true
        })
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true,
        })
    }

    render() {
        const {char, loading, error} = this.state;

        const skeleton = char || loading || error ? null : <Skeleton />; 
        const errorMessage = error ? <ErrorMessage/> : null; 
        const spinner = loading ? <Spinner/> : null; 
        const content = !(loading || error || !char) ? <View char={char}/> : null; 

        return (
            <div className="char__info">
                {skeleton}
                {errorMessage}
                {spinner}
                {content}
            </div>
        )
    }
}

const View = ({char}) => { 
    const {name, description, thumbnail, homepage, wiki, comics} = char;

    const notImage = 'http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available.jpg'
    let clazz = '';
    if (thumbnail === notImage) {
        clazz = 'char__basics_active';
    } else {
        clazz = 'char__basics';
    }

    return (
        <>
            <div className={clazz}>
                <img src={thumbnail} alt={name}/>
                <div>
                    <div className="char__info-name">{name}</div>
                    <div className="char__btns">
                        <a href={homepage} className="button button__main">
                            <div className="inner">homepage</div>
                        </a>
                        <a href={wiki} className="button button__secondary">
                            <div className="inner">Wiki</div>
                        </a>
                    </div>
                </div>
            </div>
            <div className="char__descr">
               {description}
            </div>
            <div className="char__comics">Comics:</div>
            <ul className="char__comics-list">
               {comics.length === 0 ? 'Comics is not found!' : null} 
               {comics.slice(0, 10).map((item, i) => {
                    return (
                        <li key={i} className="char__comics-item">
                            {item.name}
                        </li>
                    )
                })}
            </ul>
        </>
    )
}

export default CharInfo;
