class MarvelService {
    _apiBase = 'https://gateway.marvel.com:443/v1/public/characters';
    _apiKey = 'apikey=ef4b5738e027c92c944977fe97f44c1e'

    getResource = async (url) => { 
        let res = await fetch(url); 

        if (!res.ok) { 
            throw new Error(`Could not fetch ${url}, status: ${res.state}`);
        }

        return await res.json(); 
    }

    getAllCharacters = () => {
        return this.getResource(`${this._apiBase}?limit=9&offset=205&${this._apiKey}`);
    }

    getCharacter = (id) => {
        const res = this.getResource(`${this._apiBase}/${id}?${this._apiKey}`);
        return this._transformChar(res.data.results[0]);
    }

    _transformChar = (char) => {
        return {
            name: char.name,
            description: char.description,
            thumbnail: char.thumbnail.path + '.' + char.thumbnail.extension,
            homepage: char.urls[0],
            wiki: char.urls[1]
        }
    }

}

export default MarvelService;
