import { Component } from "react";

import ErrorMessage from "../error/ErrorMessage";

// Предохранители ловят ошибки при запуске render(), методах ЖЦ и конструкторах дочерних компонентов
// Предохранители не ловят ошибки, которые произошли внутри обработичках событий, асинхронный код (поэтому у нас есть catch())
class ErrorBoundary extends Component {
    state = {
        error: false
    }

    componentDidCatch(error, errorInfo) {
        console.log(error);
        console.log(errorInfo);
        this.setState({
            error: true
        })
    }

    render() {
        if (this.state.error) {
            return <ErrorMessage />
        }
        return this.props.children;
    }
}

export default ErrorBoundary;
