import { Component } from "react";

import AppHeader from "../appHeader/AppHeader";
import RandomChar from "../randomChar/RandomChar";
import CharList from "../charList/CharList";
import CharInfo from "../charInfo/CharInfo";

import decoration from '../../resources/img/vision.png';

// Делаем Компонент классовым - нам нужен будет стейт
class App extends Component {
    state = {
        selectedChar: null
    }

    // Метод на изменение стейта по айди
    onCharSelected = (id) => {
        this.setState({
            selectedChar: id
        })
    }

    render() {
        // 1. Прокидываем через пропс метод, получаем id из компонента CharList (который будет менять состояние в нашем компоненте App) - поднятие состояния
        // 2. Состояние этого компонента selectedChar: id передаем в компонент CharInfo
        return (
            <div className="app">
                <AppHeader/>
                <main>
                    <RandomChar/>
                    <div className="char__content">
                        <CharList onCharSelected={this.onCharSelected}/> 
                        <CharInfo charId={this.state.selectedChar}/>
                    </div>
                    <img className="bg-decoration" src={decoration} alt="vision"/>
                </main>
            </div>
        )
    }
}

export default App;
