import { Component } from 'react';

import Char from '../charItem/charItem';

import './charList.scss';
import MarvelService from '../services/MarvelService';
import abyss from '../../resources/img/abyss.jpg';


// marvelService.getAllCharacters().then(item => item.map(item => {
//     return console.log(item);
// }))


class CharList extends Component {
    state = {
       char: {}
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.test();
    }

    onLoaded = async (char) => {
        const asd = await char.map(({name, thumbnail, id}) => {
            return {
                name,
                thumbnail,
                id
            }
        });
        asd.map(item => console.log(item));
        this.setState({
            char: asd
        })
    }

    test = () => {
        this.marvelService
            .getAllCharacters()
            .then(this.onLoaded)
    }

    // test = () => {
    //     this.marvelService
    //         .getAllCharacters()
    //         .then(item => item.map(({name, thumbnail, id}) => {
    //             return console.log(name, thumbnail, id)
    //         }))
    // }


    render() {
        const {char} = this.state;
        console.log(char);
        return (
            <div className="char__list">
                <ul className="char__grid">
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item char__item_selected">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                    <li className="char__item">
                        <img src={abyss} alt="abyss"/>
                        <div className="char__name">Abyss</div>
                    </li>
                </ul>
                <button className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }
}

export default CharList;
