import { Component } from 'react';

import Spinner from '../spinner/Spinner';
import ErrorMessage from '../error/ErrorMessage'

import './charList.scss';
import MarvelService from '../services/MarvelService';

class CharList extends Component {
    state = {
        charList: [],
        loading: true,
        error: false
    }

    marvelService = new MarvelService();

    componentDidMount() {
        this.marvelService
            .getAllCharacters()
            .then(this.onCharListLoaded)
            .catch(this.onError)
    }

    onCharListLoaded = (charList) => {
        this.setState({
            charList,
            loading: false
        })
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true,
        })
    }


    render() {
        const {charList, loading, error} = this.state;

        const load = loading ? <Spinner /> : null;
        const errorMessage = error ? <ErrorMessage /> : null;
        const content = loading || error || <CharItems charList={charList}/>;

        return (
            <div className="char__list">
                <ul className={loading ? 'char__grid_load' : 'char__grid'}>
                    {load}
                    {errorMessage}
                    {content}
                </ul>
                <button className="button button__main button__long">
                    <div className="inner">load more</div>
                </button>
            </div>
        )
    }
}

const CharItems = ({charList}) => {
    let imgNotAvailable = 'http://i.annihil.us/u/prod/marvel/i/mg/b/40/image_not_available.jpg';
    const elements = charList.map(({name, thumbnail, id}) => {

    return (
        <li key={id} className={(thumbnail === imgNotAvailable) ? 'char__item-not-load' : 'char__item'}>
            <img src={thumbnail} alt="abyss"/>
            <div className="char__name">{name}</div>
        </li>
    )
   });

   return (
        <ul className={'char__grid'}>
            {elements}
        </ul>
   )
}


export default CharList;
