class ErrorBoundary extends Component { 
    state = { 
        error: false
    }

    componentDidCatch(error, errorInfo) { 
        console.log(error);
        console.log(errorInfo);
        this.setState({ 
            error: true
        })
    }

    render() { 
        console.log(this.props)
        if (this.state.error) { 
            return <ErrorMessage />
        }
        return this.props.children; 
    }
}

export default ErrorBoundary;
