// Жизненный цикл компонентов (lifecycle hooks) 
// 1. Компонент появился на странице
// 2. Компонент обновился (новые пропсы, изменение стейта) - при каждом изменении компонент обновляется и перерисовывается
// 3. Компонент удаляется со страницы

// На каждом цикле можно вызывать определенные функции:
// 1. componentDidMount() - компонент появился на странице
// 2. componentDidUpdate() - компонент обновился
// 3. componentWillMount() - компонент был удален


// Подробное описание жизненного цикла
// Первая стадия Монтирование (создание компонента)
    // Последовательность: constructor-render-react update DOM and refs-componentDidMount
// Вторая стадия Обновление (обновление пропсов, стейта)
    // Последовательность: render-render-react update DOM and refs-componentDidUpdate
// Третья стадия Размонтирование (удаление компонента)
    // Последовательность: componentWillUnmount


import { Component } from 'react';

import Spinner from '../spinner/Spinner';
import ErrorMessage from '../error/ErrorMessage';
import MarvelService from '../services/MarvelService';

import './randomChar.scss';
import mjolnir from '../../resources/img/mjolnir.png';

class RandomChar extends Component {
    constructor(props) {
        super(props);
        this.updateChar();
        // setInterval(this.updateChar, 3000) // Автоматически обновляем компонент каждые 3 секунды с показом нового персонажа (т.к интервал повешен на этот компонент, то каждый раз когда он пересоздаедтся, старый компонент тоже сохраняется в памяти на него есть ссылка на нем работает интервал)
        console.log('constructor');
    }

    state = {
        char: {},
        loading: true,
        error: false,
    }

    marvelService = new MarvelService();

    // componentDidMount() - это лучшее место, чтобы вызывать сетевые сетевые функции 
    componentDidMount() {
        console.log('mount');
    }

    componentWillUnmount() {
        console.log('unmount');
    }


    onLoaded = (char) => {
        // console.log('update'); // Для проверки в логе, как срабатывает метод setInterval
        this.setState({
            char,
            loading: false,
        });
    }

    onError = () => {
        this.setState({
            loading: false,
            error: true,
        })
    }

    updateChar = () => {
        const id = Math.floor(Math.random() * (1011400 - 1011000) + 1011000); 
        this.marvelService
            .getCharacter(id)
            .then(this.onLoaded)
            .catch(this.onError)
    }


    render() {
        console.log('render');
        const {char, loading, error} = this.state;

        const errorMessage = error ? <ErrorMessage/> : null;
        const spinner = loading ? <Spinner/> : null;
        const content = !(loading || error) ? <View char={char}/> : null; 

        return (
            <div className="randomchar">
                {errorMessage} 
                {spinner} 
                {content}
                <div className="randomchar__static">
                    <p className="randomchar__title">
                        Random character for today!<br/>
                        Do you want to get to know him better?
                    </p>
                    <p className="randomchar__title">
                        Or choose another one
                    </p>
                    <button className="button button__main">
                        <div className="inner">try it</div>
                    </button>
                    <img src={mjolnir} alt="mjolnir" className="randomchar__decoration"/>
                </div>
            </div>
        )
    }
}

const View = ({char}) => { 
    const {name, description, thumbnail, homepage, wiki} = char;

    return (
    <div className="randomchar__block">
        <img src={thumbnail} alt="Random character" className="randomchar__img"/>
        <div className="randomchar__info">
            <p className="randomchar__name">{name}</p>
            <p className="randomchar__descr">
                {description}
            </p>
            <div className="randomchar__btns">
                <a href={homepage} className="button button__main">
                    <div className="inner">homepage</div>
                </a>
                <a href={wiki} className="button button__secondary">
                    <div className="inner">Wiki</div>
                </a>
            </div>
        </div>
    </div>
    )
}

export default RandomChar;
